import { IShoppingCouponICreate } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCoupon.ICreate";

export async function test_api_coupon_create_with_valid_and_invalid_data(connection: api.IConnection): Promise<void> {
  // Step 1:  
  const sellerJoinInput: IShoppingSeller.IJoin = {};
  const seller = await api.functional.shoppings.sellers.authenticate(connection, sellerJoinInput);
  typia.assert<IShoppingSeller.IInvert>(seller);
  const sellerId = seller.id;

  // Step 2:   
  const validCouponInput: IShoppingCouponICreate = {
    discount: { unit: "percent", value: 10, threshold: null, limit: null },
    restriction: {
      access: "public",
      exclusive: false,
      volume: null,
      volume_per_citizen: null,
      expired_in: null,
      expired_at: null
    },
    criterias: [
      { type: "seller", direction: "include", seller_ids: [sellerId] }
    ],
    disposable_codes: [],
    name: "Test Coupon",
    opened_at: null,
    closed_at: null
  };
  const coupon = await api.functional.shoppings.sellers.coupons(connection, validCouponInput);
  typia.assert<IShoppingCoupon>(coupon);
  TestValidator.equals("created coupon name")<string>(validCouponInput.name)(coupon.name);

  // Step 3:     
  const missingFieldCases: Partial<IShoppingCouponICreate>[] = [
    Object.assign({}, validCouponInput, { discount: undefined }),
    Object.assign({}, validCouponInput, { restriction: undefined }),
    Object.assign({}, validCouponInput, { criterias: undefined }),
    Object.assign({}, validCouponInput, { disposable_codes: undefined }),
    Object.assign({}, validCouponInput, { name: undefined }),
    Object.assign({}, validCouponInput, { opened_at: undefined }),
    Object.assign({}, validCouponInput, { closed_at: undefined })
  ];
  for (const input of missingFieldCases) {
    await TestValidator.httpError("    400") (400)(() =>
      api.functional.shoppings.sellers.coupons(connection, input as any)
    );
  }

  // Step 4:   (, 0, 101%)
  const invalidDiscountInputs: IShoppingCouponICreate[] = [
    {
      ...validCouponInput,
      discount: { unit: "percent", value: -1, threshold: null, limit: null }
    },
    {
      ...validCouponInput,
      discount: { unit: "percent", value: 0, threshold: null, limit: null }
    },
    {
      ...validCouponInput,
      discount: { unit: "percent", value: 101, threshold: null, limit: null }
    }
  ];
  for (const input of invalidDiscountInputs) {
    await TestValidator.httpError("  400")(400)(() =>
      api.functional.shoppings.sellers.coupons(connection, input)
    );
  }

  // Step 5:     
  const duplicateCodeInput: IShoppingCouponICreate = {
    ...validCouponInput,
    disposable_codes: ["DUPLICATE_CODE"]
  };
  await api.functional.shoppings.sellers.coupons(connection, duplicateCodeInput);
  await TestValidator.httpError(" disposable_code     409")(409)(() =>
    api.functional.shoppings.sellers.coupons(connection, duplicateCodeInput)
  );
}

