import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingSeller } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSeller";
import { IShoppingCoupon } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCoupon";
import { IShoppingCouponICreate } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCoupon.ICreate";
import typia from "typia";

export async function test_api_delete_coupon_with_various_states(connection: api.IConnection): Promise<void> {
    // 1. Authenticate as Seller A
    const sellerA: IShoppingSeller.IInvert = await api.functional.shoppings.sellers.authenticate(
        connection,
        {} // Assuming empty body is OK for joining
    );
    typia.assert<IShoppingSeller.IInvert>(sellerA);

    // 2. Create a coupon as Seller A
    const couponInput: IShoppingCouponICreate = {
        discount: {
            unit: "amount",
            value: 1000,
            threshold: null,
            limit: null,
            multiplicative: false
        },
        restriction: {
            access: "public",
            exclusive: false,
            volume: 10,
            volume_per_citizen: 1,
            expired_in: 7,
            expired_at: null
        },
        criterias: [
            {
                type: "seller",
                seller_ids: [sellerA.id],
                direction: "include"
            }
        ],
        disposable_codes: [],
        name: "Discount Coupon A",
        opened_at: null,
        closed_at: null
    };
    const coupon: IShoppingCoupon = await api.functional.shoppings.sellers.coupons.post(
        connection,
        couponInput
    );
    typia.assert<IShoppingCoupon>(coupon);

    // 3. Delete the created coupon as Seller A (expect success)
    await TestValidator.error("deletion by correct seller should succeed")(
        async () => {
            await api.functional.shoppings.sellers.coupons["delete"](
                connection,
                coupon.id
            );
        }
    ); // Actually, for success, no error should be thrown, so call directly.
    await api.functional.shoppings.sellers.coupons["delete"](connection, coupon.id);

    // 4. Attempt to delete the same coupon again as Seller A (expect error)
    await TestValidator.httpError("deleting already deleted coupon should fail")(404)(
        async () => {
            await api.functional.shoppings.sellers.coupons["delete"](connection, coupon.id);
        }
    );

    // 5. Authenticate as Seller B
    const sellerB: IShoppingSeller.IInvert = await api.functional.shoppings.sellers.authenticate(
        connection,
        {}
    );
    typia.assert<IShoppingSeller.IInvert>(sellerB);

    // 6. Attempt to delete Seller A's coupon as Seller B (expect forbidden or 404)
    await TestValidator.httpError("deleting another seller's coupon should fail")(403, 404)(
        async () => {
            await api.functional.shoppings.sellers.coupons["delete"](connection, coupon.id);
        }
    );
}

