import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IShoppingSaleInquiryComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import { IShoppingSaleInquiryComment as ICommentNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import { IPageIShoppingSaleInquiryComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSaleInquiryComment";

export async function test_api_list_comments_of_sale_question_with_various_conditions(
  connection: api.IConnection,
): Promise<void> {
  // 1.  :  saleId, inquiryId     
  const saleId = "11111111-1111-4111-8111-111111111111";
  const inquiryId = "22222222-2222-4222-8222-222222222222";
  const commentBodies = ["First comment", "Second comment", "Third reply"];

  const file = {
    name: "doc",
    extension: "txt",
    url: "https://cdn.example.com/file1.txt",
  };

  for (const body of commentBodies) {
    const request: ICommentNS.ICreate = {
      format: "txt",
      body,
      files: [file],
    };
    await api.functional.shoppings.sellers.sales.questions.comments.post(
      connection,
      saleId,
      inquiryId,
      request,
    );
  }

  // 2.   
  const reqDefault: ICommentNS.IRequest = {};
  const pageDefault = await api.functional.shoppings.sellers.sales.questions.comments.patch(
    connection,
    saleId,
    inquiryId,
    reqDefault,
  );
  typia.assert<IPageIShoppingSaleInquiryComment>(pageDefault);
  TestValidator.predicate("default data count")(
    () => pageDefault.data.length >= 3,
  );

  // 3.  (limit=2, page=0)
  const reqPaging: ICommentNS.IRequest = { limit: 2, page: 0 };
  const pagePaging = await api.functional.shoppings.sellers.sales.questions.comments.patch(
    connection,
    saleId,
    inquiryId,
    reqPaging,
  );
  typia.assert<IPageIShoppingSaleInquiryComment>(pagePaging);
  TestValidator.equals("pagination.limit")<number>(2)(pagePaging.pagination.limit);
  TestValidator.predicate("pagination.records>=3")(
    () => pagePaging.pagination.records >= 3,
  );
  TestValidator.equals("data.length==2")<number>(2)(pagePaging.data.length);

  // 4. (body=Second)
  const reqSearch: ICommentNS.IRequest = { search: { body: "Second" } };
  const pageSearched = await api.functional.shoppings.sellers.sales.questions.comments.patch(
    connection,
    saleId,
    inquiryId,
    reqSearch,
  );
  typia.assert<IPageIShoppingSaleInquiryComment>(pageSearched);
  TestValidator.predicate("search matched one entry")(
    () => pageSearched.data.some((c) => c.snapshots[0].body.includes("Second")),
  );

  // 5. (, )
  const reqSortAsc: ICommentNS.IRequest = { sort: ["+created_at"] };
  const pageAsc = await api.functional.shoppings.sellers.sales.questions.comments.patch(
    connection,
    saleId,
    inquiryId,
    reqSortAsc,
  );
  typia.assert<IPageIShoppingSaleInquiryComment>(pageAsc);

  const isAsc = (arr: IShoppingSaleInquiryComment[]) =>
    arr.every((v, i, a) => i === 0 || a[i - 1].created_at <= v.created_at);
  TestValidator.predicate("asc order")(() => isAsc(pageAsc.data));

  const reqSortDesc: ICommentNS.IRequest = { sort: ["-created_at"] };
  const pageDesc = await api.functional.shoppings.sellers.sales.questions.comments.patch(
    connection,
    saleId,
    inquiryId,
    reqSortDesc,
  );
  typia.assert<IPageIShoppingSaleInquiryComment>(pageDesc);
  const isDesc = (arr: IShoppingSaleInquiryComment[]) =>
    arr.every((v, i, a) => i === 0 || a[i - 1].created_at >= v.created_at);
  TestValidator.predicate("desc order")(() => isDesc(pageDesc.data));

  // 6.   saleId/inquiryId (404  )
  await TestValidator.httpError("not found saleId")(404)(async () =>
    api.functional.shoppings.sellers.sales.questions.comments.patch(
      connection,
      "00000000-0000-0000-0000-000000000000",
      inquiryId,
      {},
    ),
  );
  await TestValidator.httpError("not found inquiryId")(404)(async () =>
    api.functional.shoppings.sellers.sales.questions.comments.patch(
      connection,
      saleId,
      "00000000-0000-0000-0000-000000000000",
      {},
    ),
  );
}

