export async function test_update_comment_on_sale_question_with_author_and_validation_checks(
  connection: api.IConnection,
  saleId: string,
  inquiryId: string,
  authorConnection: api.IConnection,
  nonAuthorConnection: api.IConnection,
): Promise<void> {
  // 1.    
  const createInput: InquiryCommentNS.ICreate = {
    format: "txt",
    body: "Original comment body",
    files: [
      {
        name: "sample",
        extension: "txt",
        url: "https://domain.com/file1.txt",
      },
    ],
  };
