import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IShoppingSaleQuestion } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleQuestion";

export async function test_api_get_single_question_of_sale_with_permission_and_notfound(connection: api.IConnection): Promise<void> {
  // 1.   : seller(), customer(), stranger(  )
  const seller = await generate_random_shopping_seller(connection);
  const customer = await generate_random_shopping_customer(connection);
  const stranger = await generate_random_shopping_customer(connection);
  const sale = await generate_random_shopping_sale(connection, seller.id);

  // 2.   ()
  const questionInput = await generate_shopping_sale_question_input(connection, {
    secret: true,
  });
  const question = await api.functional.shoppings.sellers.sales.questions.post(connection, sale.id, {
    body: questionInput,
  });
  typia.assert<IShoppingSaleQuestion>(question);

  // 3-1.   customer  
  const readByCustomer = await api.functional.shoppings.sellers.sales.questions.get(
    customer,
    sale.id,
    question.id,
  );
  typia.assert<IShoppingSaleQuestion>(readByCustomer);
  TestValidator.equals("readByCustomer", (k) => k === "created_at" || k === "read_by_seller")(question)(readByCustomer);

  // 3-2.  seller  
  const readBySeller = await api.functional.shoppings.sellers.sales.questions.get(
    seller,
    sale.id,
    question.id,
  );
  typia.assert<IShoppingSaleQuestion>(readBySeller);
  TestValidator.equals("readBySeller", (k) => k === "created_at" || k === "read_by_seller")(question)(readBySeller);

  // 4. stranger( )       
  await TestValidator.httpError("stranger forbidden")(403, 404)(async () => {
    await api.functional.shoppings.sellers.sales.questions.get(
      stranger,
      sale.id,
      question.id,
    );
  });

  // 5.   question id   404 
  await TestValidator.httpError("not found")(404)(async () => {
    await api.functional.shoppings.sellers.sales.questions.get(
      seller,
      sale.id,
      "00000000-0000-0000-0000-000000000000",
    );
  });

  // 6.   sale id   404 
  await TestValidator.httpError("not found sale")(404)(async () => {
    await api.functional.shoppings.sellers.sales.questions.get(
      seller,
      "00000000-0000-0000-0000-000000000000",
      question.id,
    );
  });
}

